INSERT INTO `wp_postmeta` VALUES("1394", "180", "_aioseop_description", "A análise de gema é uma ferramenta que o viticultor dispõe para o orientar na tomada de decisão no momento da poda de produção.\r\nEsta análise é feita com auxílio de um microscópico ou lupa binocular de aumento de 30 vezes e um bisturi, no qual são feitos cortes no sentido transversal na gema/olho até que seja possível identificar ou não a presença de primórdio de inflorescência (cacho), no qual apresenta aspecto de um cacho de uvas em miniatura.");
